class Solution {
public:
    int dp[10001][3];
    int f(int i,int a,int b,int x,set<int> &st,int occ){
        // cout<<i<<" "<<x<<endl;
        if(st.find(i)!=st.end()){
            return 1e9;
        }
        if(i>10000){
            return 1e9;
        }
        if(i<0){
            return 1e9;
        }

        if(i==x){
          return 0;  
        }


        if(dp[i][occ]!=-1){
            return dp[i][occ];
        }

        st.insert(i);
        int ans=1e9;
        if(occ<=1){

        ans=min(ans,1+min(f(i+a,a,b,x,st,1),f(i-b,a,b,x,st,2)));
        }
        else{
            ans=1+min(ans,f(i+a,a,b,x,st,1));
        }
        st.erase(i);
        return dp[i][occ]=ans;
    }
    int minimumJumps(vector<int>& forbidden, int a, int b, int x) {
        set<int>st;
        // if((abs(a-b)+x)%x!=0 && x%a!=0){
        //     return -1;
        // }       
        memset(dp,-1,sizeof(dp));
        for(auto c:forbidden){
            st.insert(c);
        }   

        int res=f(0,a,b,x,st,2);
        return res>=1e9?-1:res;
    }
};
