class Solution {
    //Pair class which will have the position and the direction
    class Pair
    {
         /* NOTE : Direction is boolean so : true -> traveling forward & false -> traveling backward*/
        int position;
        boolean direction;
        public Pair(int position, boolean direction)
        {
            this.position = position;
            this.direction = direction;
        }

    }
    public int minimumJumps(int[] forbidden, int a, int b, int x) {

        //The total limit till where can go
        int limit = 2000 + 2 * b +1;

        //Visited Array to mark the forbidden nodes as well as the nodes which already visited
        boolean[] visited = new boolean[limit];

        //Marking all the forbidden values true so that whenever we will encounter these values we can skip.
        for(int n : forbidden)
        {
            visited[n] = true;
        }
        //We have to return minuimum steps this will keep the count
        int step = 0;

        //Queue of type Pai.
        Queue<Pair> queue = new LinkedList<>();

        //Initially we will start from 0th position
        queue.add(new Pair(0,false));

        //Mark 0th postion visited 
        visited[0] = true;

        //Formal BFS Loop
        while(!queue.isEmpty())
        {
            int size = queue.size();
            //Looping through the queue. 
            for(int i = 0; i < size; i++)
            {
                //Removing the top element from the queue
                Pair p = queue.poll();

                //Extracting the position and the direction of the top most element of the queue.
                int position = p.position;
                boolean direction = p.direction;

                //Checking if the current position is equal to our destination if it is then return the steps 
                if(position == x)
                    return step;

                //If Direction is false i.e. if we are moving in backward direction 
                if(direction == false)
                {
                    // Get the backward location by subtracting current position from b
                    int moveBackward = position - b;

                    //If backward value is greater than 0 and not visited then add it into the queue and 
                    //mark it visited.
                    if(moveBackward > 0 && !visited[moveBackward])
                    {
                        queue.offer(new Pair(moveBackward,true));
                        visited[moveBackward] = true;
                    }
                }

                //If neither of the condition is true then that it means we are moving forward.
                //Get the next forward postion by adding a into current position 
                int moveForward = position + a;

                //Check if moveForward value is less than the limit and not visited. If it is not 
                //Then add this value into the queue and mark it as visited.
                if(moveForward < limit && !visited[moveForward])
                {
                    queue.offer(new Pair(moveForward,false));
                    visited[moveForward] = true;
                }

             }
             //Increment the step after looping the queue.
             step++;
        }
        return -1;
    }
}
