class Solution {
public:
    vector<int> decrypt(vector<int>& code, int k) {

        int mod_k = abs(k);

        vector<int> decrypted_code(code.size());

        if (k == 0)
            return decrypted_code;
        else if (k < 0)
        {
            // circular right
            // formula: right = (start - distance + n) % n
            for (int i = 0; i < code.size(); i++)
            {
                for (int j = 1; j <= mod_k; j++)
                    decrypted_code[i] += code[(i - j + code.size()) % code.size()]; 
            }
        }
        else
        {
            // circular left
            // formula: left = (start + distance) % n
            for (int i = 0; i < code.size(); i++)
            {
                for (int j = 1; j <= mod_k; j++)
                    decrypted_code[i] += code[(i + j) % code.size()]; 
            }
        }

        return decrypted_code;
    }
};
