impl Solution {
    pub fn decrypt(code: Vec<i32>, k: i32) -> Vec<i32> {
        let l  = code.len();

        if k == 0 {
            return vec![0; l];
        };

        let mut res = Vec::with_capacity(l);
        let mut start = match k.signum() {
            -1 => l as i32 + k,
            s => s,
        } as usize;
        let steps = k.abs() as usize;

        for _ in 0..l {
            let mut s = 0;
            (start..start + steps).for_each(|j| s += code[j % l]);
            res.push(s);
            start += 1;
        }
        res
    }
}
