class Solution:
    def decrypt(self, code: List[int], k: int) -> List[int]:
        res = []
        for x,i in enumerate(code):
            if k > 0:
                if (x+k+1) > len(code):  #This means we will have to loop over the array, so we add the remaining portion
                    res.append(sum(code[x+1:x+k+1]) + sum(code[:(k-(len(code)-1-x))]))
                else:
                    res.append(sum(code[x+1:x+k+1]))        
            elif k == 0:
                return [0]*len(code)
            elif k < 0:
                if k+x < 0: #This means we will have to loop over the array, so we add the remaining portion
                    res.append(sum(code[k+x:]) + sum(code[:x]))
                else:
                    res.append(sum(code[x+k:x]))
        return res
