class Solution(object):
    def decrypt(self, code, k):
        """
        :type code: List[int]
        :type k: int
        :rtype: List[int]
        """
        n = len(code)
        output = [0]*n
        if k == 0:
            return output

        for i in range(n):
            newNum = 0
            for j in range(1,abs(k)+1):
                if k > 0:
                    newNum = newNum + code[(i+j)%n]
                else:
                    newNum = newNum + code[(i-j)%n]
            output[i] = newNum
        return output

