class Solution {
public:
    vector<int> decrypt(vector<int>& code, int k) {
        vector<int> v(code.size(), 0);
        if (k == 0){
            return v;
        }else if ( k > 0){
            for(int i=0; i<code.size(); i++){
                for(int j=1; j<=k; j++){
                    v[i] += code[(i+j)%code.size()];
                }
            }
            return v;
        }else{
            k = 0-k;
            for(int i=0; i<code.size(); i++){
                for(int j=1; j<=k; j++){
                    //cout << "i " << i << " j " << j << endl;
                    if ((i-j) >= 0){
                        v[i] += code[(i-j)%code.size()];
                    }else{
                        v[i] += code[code.size() - abs(i-j)%code.size()];
                    }
                }
            }
            return v;
        }
    }
};
