                for(int j=i-1;j>=(-code.length);j--)
                {
                    if(j>=0)
                    {
                        sum+=code[j];
                    }
                    else if (j<0)
                    {
                        sum+=code[j+code.length];
                    }
                    k1++;
                    if(k1==Math.abs(k))
                    {
                        break;
                    }
                }
                result[i]=sum;
            }

        }

        return result;
    }
}
