class Solution {

public:
    int minimumDeletions(string s) {
        int prefA[s.size()+1];
        int prefB[s.size()+1];
        memset(prefA, 0, sizeof(prefA));
        memset(prefB, 0, sizeof(prefB));

        for(int i=0; i<s.size(); i++) {
            prefA[i+1] = prefA[i] + (s[i] == 'a' ? 1 : 0);
            prefB[i+1] = prefB[i] + (s[i] == 'b' ? 1 : 0);
        }

        int minMoves = INT_MAX;

        for(int i=0; i<=s.size(); i++) {
            minMoves = min(minMoves, prefB[i] + (prefA[s.size()] - prefA[i]));
        }

        return minMoves;
    }
};
```

