use std::cmp::min;
impl Solution {
    pub fn minimum_deletions(s: String) -> i32 {
        let s: Vec<char> = s.chars().collect();
        let N = s.len();
        let mut prefix = vec![0; N];
        let mut suffix = vec![0; N];
        let (beg, end) = (0, N - 1);
        for i in 0..N {
            prefix[i] = if s[i] == 'b' { 1 } else { 0 } + if beg < i { prefix[i - 1] } else { 0 };
        }
        for j in (0..N).rev() {
            suffix[j] = if s[j] == 'a' { 1 } else { 0 } + if j < end { suffix[j + 1] } else { 0 };
        }
        prefix.iter().zip(suffix).map(|(a, b)| a + b).min().unwrap() - 1 // ⭐️ -1 since we only need to delete 'a' xor 'b' at the optimal "pivot" index
    }
}
