class Solution:
    def minimumDeletions(self, s: str) -> int:
        a_cnt = s.count('a')
        result = a_cnt
        left_b = 0
        right_a = a_cnt

        for char in s:
            if char == 'a':
                right_a -= 1
            else:
                left_b += 1
            result = min(result, right_a + left_b)

        return result

