        int n = s.length();



        //1. Calculate the number of 'b's appearing before index i
        vector<int> leftB(n,0); 
        for(int i=1;i<n;i++){
            leftB[i] = (s[i-1] == 'b') + leftB[i-1];
        }

        //2. Calculate the number of 'a's appearing after index i
        vector<int> rightA(n,0);
        for(int i=n-2;i>=0;i--){
            rightA[i] = (s[i+1] == 'a') + rightA[i+1];
