class Solution {
    public int minimumDeletions(String s) {
       Stack<Character> st = new Stack<>();
       int min_del = 0;
       for(char c : s.toCharArray()){
         //whenever find 'b' on top of stack and 'a' on string we
         //delete both from the stack but we delete only 'a' or 'b' depending
         //on the no of b's on the left and the no of a's on the right
         //to eliminate b a.
         if(!st.isEmpty() && st.peek() == 'b' && c == 'a'){
           st.pop();
           min_del++;
         }else st.push(c);
        }
       return min_del;
    }
}
