vis[x][1] denotes that we have visited the position x following backward direction (X<--some other point)

```
class Solution {
public:
    int minimumJumps(vector<int>& forbidden, int a, int b, int x) {
        queue<pair<int,int>>q;
        q.push({0,0}); // pos, dir(0->forward 1->backward); // adding starting postion with direction as forward (0)
        int end=10000; // from constraints
        vector<vector<bool>>vis(end+1,vector<bool>(2,false));

        for(auto zone:forbidden){ // visit the all forbidden zone
            if(zone<=end)vis[zone][0]=true;
            if(zone<=end)vis[zone][1]=true;
        }
        if(vis[x][0])return -1; // if home is at forbidden zone
        vis[0][0]=true; // we assume we have visited at source by travelling forward as well as backwards.
        vis[0][1]=true;
        int jumps=0;
        while(!q.empty()){
            int size=q.size();
            while(size--){
                auto rem=q.front(); q.pop();
                int pos=rem.first;
                int dir=rem.second;
                if(pos==x)return jumps;
                if(dir==0){ // if this position is travelled with forward direction then we can move forward as well as backward directiona.
                    if(((pos+a)<=end)&&(!vis[pos+a][0])){vis[pos+a][0]=true;q.push({pos+a,0});}
                    if(((pos-b)>=0)&&(!vis[pos-b][1])){vis[pos-b][1]=true;q.push({pos-b,1});}

                }else{ // if this position is travelled with backward direction then we can move only in forward direction
                    if((pos+a<=end)&&(!vis[pos+a][0])){vis[pos+a][0]=true;q.push({pos+a,0});}
                }
            }
           jumps++; 

        }
        return -1;
    }
};

