class Solution {
    public int minimumJumps(int[] forbidden, int a, int b, int x) {
        if(x == 0) return 0;

        // The bug cannot go in the backward direction two times

        HashSet<String> visited = new HashSet<>();
        for(int f : forbidden){
            visited.add(f+","+1);
            visited.add(f+","+0);
        }
        // Check if target is forbidden
        if(visited.contains(x+","+0) || visited.contains(x+","+1)) return -1;

        Queue<Pair> queue = new LinkedList<>();

        // Zero is the starting point
        // 1 -> Forward
        // 0 -> Backward
        queue.offer(new Pair(0, 1));
        visited.add(0+","+1);

        int step = 0;
        while(!queue.isEmpty()){
            step++;

            int size = queue.size();
            for(int i=0; i<size; i++){
                Pair pa = queue.poll();

                /*
                   6000 because we don't want to lose any state, as the max value of a and b is 2000, suppose if we take
				   4000 value instead of 6000 and a and b are 2000, then 4000 and backward direction position will not 
				   be possible, to avoid that, take max value to be 6000 to be able to reach all the states 
                */
                if(pa.x > 6000) continue; 
                if(pa.x < 0) continue;

                // Check whether reached the target or not
                if((pa.x + a == x) || (pa.dir == 1 && pa.x - b == x)){
                   return step; 
                }

                // Go forward
                if(!visited.contains( (pa.x + a) +","+1)){
                    visited.add((pa.x + a)+","+1);
                    queue.offer(new Pair(pa.x+a, 1));
                }

                // Go backward
                // Can only go backward if the current direction is not backward
                if(pa.dir != 0 && !visited.contains((pa.x - b) + "," + 0)){
                    visited.add((pa.x - b) + "," + 0);
                    queue.offer(new Pair(pa.x - b, 0));
                }

            }
        }

        return -1;
    }

    private class Pair{
        public int x;
        public int dir;

        public Pair(int x, int dir){
            this.x = x;
            this.dir = dir;
