class Solution {
public:
    int minimumJumps(vector<int>& forbidden, int a, int b, int x) {
        int big = 6005;
        vector<vector<int>> dp(big, vector<int>(2, -1)); // Keep track of visited positions.

        for(auto f : forbidden) {
            dp[f][0] = -2;
            dp[f][1] = -2;
        }

        // vector {position, jumpBack, step}.
        queue<vector<int>> q;
        q.push({0, 0, 0});

        while(!q.empty()) {
            auto v = q.front();
            q.pop();

            if(v[0] == x) return v[2];

            // visited, including forbidden.
            if(dp[v[0]][v[1]] != -1) {
                continue;
            } 

            dp[v[0]][v[1]] = 1; // Mark as visited.

            if(v[0] + a < big) q.push({v[0]+ a, 0, v[2] + 1});
            if(v[1] == 0 && v[0] - b >= 0) q.push({v[0] - b, 1, v[2] + 1});

        }

        return -1;
    }
};
