class Solution {
public:
	int minimumJumps(vector<int>& forbidden, int a, int b, int x) {
		unordered_set<int> forbi(forbidden.begin(), forbidden.end());

		int far = *max_element(forbidden.begin(),forbidden.end())+a+b;
        far = max(far,x+a+b);
        vector<vector<int>> visited(2,vector<int>(far+1));
		queue<pair<int,bool>> qu; 

		qu.push({0,false});
		visited[0][0] = 1;
		visited[1][0] = 1;
		int ans = 0;
		while(!qu.empty()) {
			int len = qu.size();
			while(len > 0) {
				len--;
				int cur = qu.front().first;
				bool flag = qu.front().second;
				if(cur == x) {
					return ans;
				}
				qu.pop();
				int forward = cur + a;
				int backward = cur - b;
				if(forward <= far && visited[0][forward] == 0 && !forbi.count(forward)) {
					qu.push({forward,false});
					visited[0][forward] = 1;
				}
				if(backward >=0 && visited[1][backward] == 0 && !forbi.count(backward) && !flag) {
					qu.push({backward,true});
					visited[1][backward] = 1;
				}
			}
			ans++;
		}
		return -1;
	}
};
