    cell::RefCell,
    cmp::Ordering,
    collections::hash_map::Entry::Occupied,
    collections::hash_map::Entry::Vacant,
    collections::{BTreeMap, BinaryHeap, HashMap, HashSet, VecDeque},
    rc::Rc,
};


#[derive(PartialEq, Eq, Hash, Clone, Copy, Debug)]
enum Move {
    Forward,
    Backward,
}

impl Solution {
    pub fn minimum_jumps(forbidden: Vec<i32>, right: i32, left: i32, current: i32) -> i32 {
        let mut res = 0;
        let mut queue = VecDeque::new();
        let mut visited: HashSet<(i32, Move)> = HashSet::new();

        let min = 0;
        let max = std::cmp::max(*forbidden.iter().max().unwrap(), current) + right * 2 + left * 2;

        for i in forbidden {
            visited.insert((i, Move::Forward));
            visited.insert((i, Move::Backward));
        }

        queue.push_back((current, Move::Forward));
        queue.push_back((current, Move::Backward));

        while !queue.is_empty() {
            let mut next_queue = VecDeque::new();
            while let Some(current) = queue.pop_front() {
                if visited.contains(&current) {
                    continue;
                }
                visited.insert(current);
                let (curr_idx, curr_move) = current;

                if curr_idx == 0 {
                    return res;
                } else if curr_idx <= min || curr_idx >= max {
                    continue;
                }

                match curr_move {
                    Move::Forward => {
                        next_queue.push_back((curr_idx - right, Move::Forward));
                        next_queue.push_back((curr_idx + left, Move::Backward));
                    }
                    Move::Backward => {
                        next_queue.push_back((curr_idx - right, Move::Forward));
                    }
                }
            }

            queue = next_queue;
            res += 1;

            // println!("{:?}", queue);
        }

        -1
    }
}

```

