class Solution {
public:
    int minimumJumps(vector<int>& forbidden, int a, int b, int x) 
    {
        unordered_set<int>forbid;
        unordered_set<int>visited;
        for(int index = 0; index < forbidden.size(); index++)   forbid.insert(forbidden[index]);

        queue<pair<int, bool>>q;
        int level = 0;
        q.push({0, false});
        visited.insert(0);

        while(!q.empty())
        {
            int sz = q.size();
            while(sz--)
            {
                int index = q.front().first;
                bool lastBackward = q.front().second;
                q.pop();

                if(index == x)  return level;

                int forward = index + a;
                if(forward <= 1e6  && !forbid.count(forward) && !visited.count(forward))
                {
                    q.push({forward, false});
                    visited.insert(forward);
                }

                int backward = index-b;
                if(!lastBackward && backward >=0 && !forbid.count(backward) && !visited.count(backward))
                {
                    q.push({backward, true});
                    visited.insert(backward);
                }
            }
            level++;
        }

        return -1;    
    }
};
