        visited.add(0);
        while(queue.size() > 0){
            int size = queue.size();
            for(int i = 0; i < size; i++){
                int[] first = queue.remove();
                int currPos = first[0];
                int count = first[1];
                int steps = first[2];
                if(currPos == x)
