class Solution(object):
    def get_key(self, pos, b_jump):
        return str(pos) + "$" + str(b_jump)

    def is_pos_valid(self, pos, forbidden_map):
        if pos in forbidden_map or pos < 0:
            return False
        return True

    def create_forbidden_map(self, forbidden):
        forbidden_map = {}
        for pos in forbidden:
            forbidden_map[pos] = True

        return forbidden_map

    def get_min_jumps(self, a, b, x, forbidden_map, bfs):
        q = deque()
        q.append(bfs)
        visited = {}
        visited[self.get_key(0, 0)] = True
        limit = 2000 + a + b

        while len(q) > 0:
            element = q.popleft()
            pos = element[0]
            b_jumps = element[1]
            jumps = element[2]

            if pos == x:
                return jumps

            if self.is_pos_valid(pos+a, forbidden_map) and self.get_key(pos+a, 0) not in visited and pos < limit:
                visited[self.get_key(pos+a, 0)] = True
                q.append([pos+a, 0, jumps+1])
            if b_jumps != 1:
                if self.is_pos_valid(pos-b, forbidden_map) and self.get_key(pos-b, 1) not in visited and pos < limit:
                    visited[self.get_key(pos-b, 1)] = True
                    q.append([pos-b, 1, jumps+1])

        return -1

    def minimumJumps(self, forbidden, a, b, x):
        forbidden_map = self.create_forbidden_map(forbidden)
        return self.get_min_jumps(a,b,x,forbidden_map, [0, 0, 0])
