        queue = deque()
        seen = set()
        banned = set(forbidden)

        jumps = 0
        queue.append((0, True))
        max_position = a + b + max(x, max(forbidden))

        while queue:
            size = len(queue)

            for _ in range(size):
                cur, can_go_backward = queue.popleft()

                if cur == x:
                    return jumps

                forward = (cur + a, True)
                backward = (cur - b, False)

                if forward[0] not in banned and forward[0] <= max_position and forward not in seen:
