//Complexity should be O (   m(10) * numPiles(50) * Max(num(i))(1000)   ) 

import java.util.ArrayList;
import java.util.HashMap;

class Solution {
  HashMap<String, Boolean> dp;
  int target;

  public boolean canDistribute(int[] nums, int[] quantity) {

    dp = new HashMap<>();
    HashMap<Integer, Integer> hm = new HashMap<>();
    for (int n : nums) {
      hm.put(n, hm.getOrDefault(n, 0) + 1);
    }


    //Make a target bitmask when every customer is satisfied!
    target = 0;
    for (int i = 0; i < quantity.length; i++) {
      target |= (1 << i);
    }


    //Make an array that represent piles of the same integer
    int[] remaining = new int[hm.size()];
    ArrayList<Integer> ls = new ArrayList<>(hm.values());
    for (int i = 0; i < remaining.length; i++)
      remaining[i] = ls.get(i);


    return magic(0, 0, remaining[0], remaining, quantity);
  }


  boolean magic(int taken, int pos, int remain, int[] remaining, int[] quantity) {


    //Have satisfied all customers
    if (taken == target)
      return true;

    //Exhausted all piles
    if (pos == remaining.length)
      return false;


    //Search if we have already computed.
    String key = pos + " " + taken + " " + remain;
    if (dp.containsKey(key))
      return dp.get(key);


    boolean ans = false;
    boolean found = false;


    //Brute force check if this pile can satisfy any customer, if yes then  reduce pile.
    for (int i = 0; i < quantity.length; i++) {
      if ((taken & (1 << i)) == 0 && quantity[i] <= remain) {
        found = true;
        //dont change pile and iterate for remaining, after satisfying this customer.
        ans |= magic(taken | (1 << i), pos, remain - quantity[i], remaining, quantity);

        if (ans)
          break;
      }
    }

    //change pile if this pile cannot satisfy anyone anymore and we haven't reached the end!
    if (!found && pos + 1 < remaining.length) {
      ans |= magic(taken, pos + 1, remaining[pos + 1], remaining, quantity);
    }
    dp.put(key, ans);
    return ans;
  }
}
