class Solution {
    public boolean canDistribute(int[] nums, int[] quantity) {
        Map<Integer, Integer> cnt = new HashMap<>();
        for (int x : nums) {
            cnt.merge(x, 1, Integer::sum);
        }
        int n = cnt.size();
        int m = quantity.length;
        int[] freq = new int[n];
        int j = 0;
        for (int val : cnt.values()) {
            freq[j] = val;
            j++;
        }
        boolean[][] dp = new boolean[n + 1][1 << m];
        for (int i = 0; i <= n; i++) {
            dp[i][0] = true;
        }
        for (int i = 1; i <= n; i++) {
            for (int state = 1; state < 1 << m; state++) {
                if (dp[i - 1][state]) {
                    dp[i][state] = true;
                    continue;
                }
                for (int subset = state; subset > 0; subset = (subset - 1) & state) {
                    if (!dp[i - 1][state - subset]) {
                        continue;
                    }
                    if (satisfy(freq[i - 1], quantity, subset)) {
                        dp[i][state] = true;
                        break;
                    }
                }
            }
            if (dp[i][(1 << m) - 1]) {
                return true;
            }
        }
        return false;
    }

    private boolean satisfy(int cnt, int[] quantity, int bitmask) {
        for (int i = 0; i < quantity.length; i++) {
            if (((bitmask >> i) & 1) == 1) {
                cnt -= quantity[i];
                if (cnt < 0) {
                    return false;
                }
            }
        }
        return true;
    }
}
