        for (int i:nums) m[i]++ ;
        for (auto y = m.begin();y!=m.end();y++) occurrences.push_back(y->second) ;
        sort(occurrences.begin(),occurrences.end()) ;
        sort(qt.begin(),qt.end()) ;
        reverse(qt.begin(),qt.end()) ;
        f(qt) ;
        return ans ;
    }
};

