public boolean canDistribute(int[] nums, int[] quantity) {
	int[] count = new int[1001];
	for (int num : nums)
		++count[num];
	Arrays.sort(quantity);
	Map<Integer, Integer> map = new HashMap<>();
	for (int num : count)
		map.put(num, map.getOrDefault(num, 0) + 1);
	return dfs(quantity, map, quantity.length - 1);
}

private boolean dfs(int[] quantity, Map<Integer, Integer> map, int curr) {
	if (curr == -1)
		return true;
	for (int key : new HashSet<>(map.keySet()))
		if (key >= quantity[curr]) {
			map.put(key, map.get(key) - 1);
			map.remove(key, 0);
			map.put(key - quantity[curr], map.getOrDefault(key - quantity[curr], 0) + 1);
			if (dfs(quantity, map, curr - 1))
				return true;
			map.put(key - quantity[curr], map.get(key - quantity[curr]) - 1);
			map.remove(key - quantity[curr], 0);
			map.put(key, map.getOrDefault(key, 0) + 1);
		}
	return false;
}
