/**
 * Time Complexity: O(n_distinct_nums * (3 ^ n_quantity))
 * Space Complexity: O(n_distinct_nums + (2 ^ n_quantity))
 * where `n_distinct_nums` is the number of distinct elements of the vector `nums`
 *       `n_quantity` is the length of the vector `quantity`
 */
class Solution {
 public:
  bool canDistribute(const vector<int> &nums, const vector<int> &quantity) {
    constexpr int range = 2;
    unordered_map<int, int> num_to_count;
    for (const int num : nums) {
      ++num_to_count[num];
    }

    vector<int> counts;
    for (const auto [_, count] : num_to_count) {
      counts.emplace_back(count);
    }

    const int n_quantity = static_cast<int>(quantity.size());
    const int layouts = 1 << n_quantity;
    int layout_to_count[layouts];
    memset(layout_to_count, 0, sizeof(layout_to_count));
    for (int i = 0; i < n_quantity; ++i) {
      for (int sub_layout = 0; sub_layout < (1 << i); ++sub_layout) {
        layout_to_count[(1 << i) | sub_layout] = quantity[i] + layout_to_count[sub_layout];
      }
    }

    bool dp[range][layouts];
    memset(dp, 0, sizeof(dp));
    int previous = 0;
    int current = 1;
    dp[previous][0] = true;
    const int n_distinct_nums = static_cast<int>(counts.size());
    for (int i = 0; i < n_distinct_nums && !dp[previous][layouts - 1]; ++i) {
      const int count = counts[i];
      dp[current][0] = true;
      for (int current_layout = 1; current_layout < layouts; ++current_layout) {
        dp[current][current_layout] = dp[previous][current_layout];
        for (int sub_layout = current_layout; sub_layout > 0; sub_layout = (sub_layout - 1) & current_layout) {
          if (layout_to_count[sub_layout] <= count && dp[previous][current_layout ^ sub_layout]) {
            dp[current][current_layout] = true;
          }
        }
      }

      previous ^= 1;
      current ^= 1;
      memset(dp[current], 0, sizeof(dp[current]));
    }
    return dp[previous][layouts - 1];
  }
};
```

