class Solution {
public:

    bool Solve(vector<int>& quantity, map<int, int>& pairs, int qindex){
        if(qindex == quantity.size()){
            return true;
        }

        for(auto& element : pairs){
            if(quantity[qindex] <= element.second){
                element.second -= quantity[qindex];
                if(Solve(quantity, pairs, qindex + 1)){
                    return true;
                }
                element.second += quantity[qindex];
            }
        }
        return false;
    }

    bool canDistribute(vector<int>& nums, vector<int>& quantity) {
        map<int, int> pairs;

        for(auto& n : nums){
            ++pairs[n];
        }

        std::sort(quantity.begin(), quantity.end(), greater<>());

        return Solve(quantity, pairs, 0);
    }
};
