class Solution {
public:
    unordered_map<int,int> mp;
    bool solve(int pos, vector<int> &v, vector<int> &q){
        if(pos == q.size())
            return true;
        for(int i=0; i < v.size(); i++){
            if(i && v[i] == v[i-1])    // skip the elements which we have already checked
                continue;
            if(v[i] >= q[pos]){
                v[i] -= q[pos];
                if(solve(pos+1, v, q))
                    return true;
                v[i] += q[pos];
            }
        }
        return false;
    }

    bool canDistribute(vector<int>& nums, vector<int>& quantity) {
        for(int i : nums)
            mp[i]++;
        vector<int> v;
        for(auto i : mp)
            v.push_back(i.second);
        sort(quantity.rbegin(), quantity.rend());
        return solve(0, v, quantity);
    }
};
