class Solution:
    def canDistribute(self, nums: List[int], quantity: List[int]) -> bool:
        cnt = Counter(nums)
        l = []
        for k, v in cnt.items():
            l.append(v)
        l = sorted(l, reverse=True)
        n = len(quantity)

        @lru_cache(None)
        def dp(i, left):
            if i == n:
                return True
            left = list(left)
            for j, c in enumerate(left):
                if c >= quantity[i]:
                    left[j] -= quantity[i]
                    if dp(i+1, tuple(sorted(left))):  # Sort the tuple to avoid unnecessary repitition since it doesn't matter which number is assigned to each order.
                        return True
                    left[j] += quantity[i]
            return False

        return dp(0, tuple(l))
