        for(int i = n-2; i >= 0; i--){
            if(nums[i] == nums[i+1]) cond[i] = cond[i+1];
            else cond[i] = i;
        }
        int q = quantity.size();
        dp = vector(n, unordered_set<int>());
        return find(0, nums, quantity, cond, 0, n, q);

    }
