class Solution {
public:
    int getQuantityUsed(int mask, vector<int>&quantity){
        int sz = quantity.size();
        int total = 0;
        int i, j;
        for(i = 0; i < sz; i++){
            j = sz - 1 - i;
            if((1 << i) & mask){
                total += quantity[j];
            }
        }
        return total;
    }

    bool canDistribute(vector<int>&nums, vector<int>&quantity){
        int i, j, k, l;
        int sz = quantity.size();
        int len = nums.size();
        int bitmask = 1 << sz;
        unordered_map<int, int>hashMap;
        vector<int>uniqueNums; 

        for(i = 0; i < len; i++){
            if(hashMap.find(nums[i]) == hashMap.end()){
                hashMap[nums[i]] = 1;
                uniqueNums.push_back(nums[i]);
            }
            else{
                hashMap[nums[i]]++;
            }
        }        

        len = uniqueNums.size();
        vector<vector<bool>>isValid(len, vector<bool>(bitmask));
        for(i = 0; i < len; i++){
            for(j = 0; j < bitmask; j++){
                if(getQuantityUsed(j, quantity) <= hashMap[uniqueNums[i]]){
                    isValid[i][j] = true;
                }
            }
        }

        vector<vector<bool>>dp(bitmask, vector<bool>(len + 1));
        for(i = 0; i <= len; i++){
            dp[0][i] = true;
        }
        for(i = 1; i < bitmask; i++){
            for(j = 1; j <= len; j++){
                dp[i][j] = dp[i][j - 1];
                for(k = i; k; k = (k - 1) & i){
                    if(isValid[j - 1][k]){
                        dp[i][j] = dp[i][j] || dp[i - k][j - 1];
                    }
                }
            }
        }
        return dp[bitmask - 1][len];
    }
};

