	bool canDistribute(vector<int>& nums, vector<int>& quantity) {
        unordered_map<int,int> hm;
        for(int n:nums){
            hm[n]++;
        }
        vector<int> freq;
        for(auto p:hm){
            freq.push_back(p.second);
        }
        int n=freq.size(),m=quantity.size();
        vector<vector<int>> poss(n);
        for(int i=0;i<n;i++){
            for(int j=0;j<(1<<m);j++){
                int sum=0;
                for(int k=0;k<m && sum<=freq[i];k++){
                    if(j&(1<<k)){
                        sum+=quantity[k];
                    }
                }
                if(sum<=freq[i]){
                    poss[i].push_back(j);
                }
            }
        }
        bool dp[n+1][1<<m];
        for(int i=n;i>=0;i--){
            for(int j=0;j<(1<<m);j++){
                if(i==n){
                    dp[i][j]=j+1==1<<m;
                }else{
                    dp[i][j]=false;
                    for(int k:poss[i]){
                        if(dp[i][j]){
                            break;
                        }
                        if((k&j)==0 && dp[i+1][j|k]){
                            dp[i][j]=true;
                        }
                    }
                }
            }
        }
        return dp[0][0];
    }
