```
class Solution {
    public boolean canDistribute(int[] nums, int[] quantity) {
        int sum=0;
        for(int q:quantity) sum+=q;
		// if sum is greater than number count, not possible
        if(sum>nums.length) return false;
        Map<Integer,Integer> cnts=new HashMap<>();
        for(int num:nums) {
			// group numbers
            cnts.put(num,cnts.getOrDefault(num,0)+1);
        }
        Map<Integer,Integer> map=new HashMap<>();
        for(var cnt:cnts.values()) {
			// group counts
            map.put(cnt,map.getOrDefault(cnt,0)+1);
        }
		// sort quantity
        Arrays.sort(quantity);
		// let's start from high to low to improve performace
        return dfs(map,quantity,quantity.length-1);
    }

    boolean dfs(Map<Integer,Integer> map,int[]  arr,int idx) {
        if (idx<0) return true;
        if(map.containsKey(arr[idx])) {
            // greedy, take exactly match
            var cnt=map.get(arr[idx]);
            if(cnt==1) {
                map.remove(arr[idx]);
            } else {
                map.put(arr[idx],cnt-1);
            }
            if(dfs(map,arr,idx-1)) return true;
            // if it doesn't work, backtracking
            map.put(arr[idx],cnt);
            return false;
        } else {
            var keys=new ArrayList<>(map.keySet());
            for(int key:keys) {
                if(key<arr[idx]) continue;
				// take away from this cnt and update map accordingly
                var cnt=map.get(key);
                if(cnt==1) {
                    map.remove(key);
                } else {
                    map.put(key,cnt-1);
                }
                var r=key-arr[idx];
                var rc=map.getOrDefault(r,0);
                map.put(r,rc+1);
                if(dfs(map,arr,idx-1)) return true;
                // if it doesn't work, backtracking
                map.put(key,cnt);
                if(rc==0) {
                    map.remove(r);
                } else {
                    map.put(r,rc);
                }
            }
            return false;
        }
    }
}
