class Solution:
    def canDistribute(self, nums: List[int], quantity: List[int]) -> bool:

        # 1. Find the frequency of each number
        freqs = collections.Counter(list(collections.Counter(nums).values()))

        # 2. If the quantity[i] is in freq, use that number.
        #    i.e. quantity = [2, 2] ; nums = [1, 1, 2, 3] use [1, 1] right away
        #         so we get quantity = [2] and nums = [2, 3]
        removed = set()
        for i in range(len(quantity)):
            if freqs[quantity[i]] > 0:
                freqs[quantity[i]] -= 1
                removed.add(i)
        quantity = [quantity[i] for i in range(len(quantity)) if i not in removed]

        if not quantity: return True 

        # 3. Sort the quantities in reverse order, this helps identify False cases much faster.
        quantity.sort(reverse = True)

        # 4. Breadth First Search
        q = Max_Heap([(0, freqs)])
        while not q.is_empty():
            i, freqs = q.heappop()

            if i == len(quantity):
                return True
            quant = quantity[i]

            if quant in freqs and freqs[quant] > 0:
                new_freqs = freqs.copy()
                new_freqs[quant] -= 1
                q.heappush((i+1, new_freqs))
                continue

            for f in freqs:
                if f > quant and freqs[f] > 0:
                    new_freqs = freqs.copy()
                    new_freqs[f] -= 1
                    new_freqs[f-quant] += 1
                    q.heappush((i+1, new_freqs))

        return False
