class Max_Heap(object):
    def __init__(self, arr):
        self.arr = arr if arr else []

    def heappush(self, val):
        self.arr.append(val)
        i = len(self.arr) - 1
        while True:
            i, b = self.bubble_up(i)
            if not b: 
                break

    def heappop(self):
        if len(self.arr) == 1:
            return self.arr.pop()
        res = self.arr[0]
        self.arr[0] = self.arr.pop()
        i = 0
        while True:
            i, b = self.bubble_down(i)
            if not b:
                break

        return res

    def bubble_up(self, i):
        if not i: return (i, False)

        j = (i + 1) // 2
        if self.arr[j][0] < self.arr[i][0]:
            self.arr[i], self.arr[j] = self.arr[j], self.arr[i]
            return (j, True)

        return (i, False)

    def bubble_down(self, i):
        j = (i+1) * 2
        if j - 1 >= len(self.arr): 
            return (i, False)

        if j == len(self.arr):
            # only consider left branch
            if self.arr[j-1][0] > self.arr[i][0]:
                self.arr[i], self.arr[j-1] = self.arr[j-1], self.arr[i]
            return (j, False)

        # consider left and right branch
        j = max((j, j-1), key = lambda x: self.arr[x][0])
        if self.arr[j][0] > self.arr[i][0]:
            self.arr[i], self.arr[j] = self.arr[j], self.arr[i]
            return (j, True)
        return (i, False)

    def is_empty(self):
        return not bool(self.arr)
