class Solution {
    public int[] decrypt(int[] code, int k) {
        int[] presum = new int[code.length];
        int[] postsum = new int[code.length];

		//Create prefix sum array
        int sum = 0;
        for(int i=0; i<code.length; ++i){
            sum += code[i];
            presum[i] = sum;
        }

		//Create prefix sum array in reverse
        sum=0;
        for(int i=code.length-1; i>=0; --i){
            sum += code[i];
            postsum[i] = sum;
        }

        for(int i=0; i<code.length; ++i){
            if(k > 0){
                if((i+k) < code.length){
                    code[i] = presum[i+k]-presum[i];
                }else{
					//presum[code.length-1] - presum[i]  --> Elements right of i
					//presum[k-(code.length -i)]; --> Elements left of i
                    code[i] = presum[code.length-1] - presum[i] + presum[k-(code.length -i)];
                }   
            }else if(k == 0){
                code[i] = 0;
            }else{
                if(i+k >= 0){
                    code[i] = postsum[i+k] - postsum[i];
                }else{
					//postsum[0] - postsum[i]  --> Elements right of i
					//postsum[code.length+(i+k)] --> Elements left of i
                    code[i] = postsum[0] - postsum[i] + postsum[code.length+(i+k)];
                }
            }
        }

        return code;
    }
}
```

