import math
class Solution:
    def routine(self, code, k):
        append_times = int(math.ceil(abs(k) / len(code)))
        original_length = len(code)

        for i in range(1, append_times + 1):
            code += code

        prefixSum = [0]
        for num in code:
            prefixSum.append(prefixSum[-1] + num)

        if k > 0:
            for i in range(original_length):
                code[i] = prefixSum[i + k + 1] - prefixSum[i + 1]
        else:
            for i in range(original_length * append_times, len(code)):
                #    [0, 1, 0, 1]
                # [0, 0, 1, 1, 2]
                code[i] = prefixSum[i] - prefixSum[i + k]
        return code[: original_length] if k > 0 else code[-original_length:]

    def decrypt(self, code: List[int], k: int) -> List[int]:
        if not k:
            return [0] * len(code)
        return self.routine(code, k)

