    public int[] decrypt(int[] code, int k) {
        int[] cycle = new int[code.length];  
        if(k == 0) return cycle;

        int sum = 0;
        int t = Math.abs(k);

        for(int i = 0; i < t; i ++)
            sum += code[i];

        for(int i = 0; i < code.length; i ++){
            sum = sum - code[i] + code[(i + t)%code.length];
            cycle[i] = sum;
        }

        if(k > 0) return cycle;

        int[] res = new int[code.length];

        for(int i = 0; i < code.length; i ++)
            res[i] = cycle[(i + code.length - t - 1)%code.length];

        return res;
    }
