class Solution {
public:
    vector<int> decrypt(vector<int>& code, int k) {
        int sum = 0;
        int n = code.size();
        int cnt = 0;
        vector<int> v(n);

        if (k > 0) {
            for (int i = 0; i < n; ++i) {
                for (int j = i+1; cnt < k; j++) {
                    if (j == n) j = 0; // circle logic - code[n+1] = code[0]
                    sum += code[j];
                    cnt++;
                }
                v[i] = sum;
                sum = 0;
                cnt = 0;
            }
        }

        else if (k < 0) {
            for (int i = 0; i < n; ++i) {
                for (int j = i-1; cnt < -k; j--) {
                    if (j < 0) j = n-1; // circle logic - code[-1] = code[n-1]
                    sum += code[j];
                    cnt++;
                }
                v[i] = sum;
                sum = 0;
                cnt = 0;
            } 
        }

        else {
            for (int i = 0; i < n; ++i) {
                v[i] = 0;    
            }
        }

        return v;
    }
};
