```
impl Solution {
    pub fn decrypt(code: Vec<i32>, k: i32) -> Vec<i32> {
        if code.len() <= 0 {
            return code
        }
        let mut ret = vec![0; code.len()];
        let L  = ret.len();

        match k {
            0 => {}
            std::i32::MIN..=-1_i32 => {
                for i in 0..L {
                    for j in k..=-1 {
                        ret[i] += code[((i+L) as i32 + j ) as usize % L]
                    }
                }
            }
            1_i32..=std::i32::MAX => {
                for i in 0..L {
                    for j in 1..=k {
                        ret[i] += code[(i+j as usize)  % L]
                    }
                }
            }
        }

        ret
    }
}



