        int n = code.length;
        int[] answer = new int[n];

        for (int i = 0; i < n; i++) {
            int sum = 0;

            if (k > 0) {
                for (int j = 0; j < k; j++) {
                    sum += code[(i + j + 1) % n];
                }
            } else if (k < 0) {
                for (int j = 0; j < -k; j++) {
