class Solution {
    public int[] decrypt(int[] code, int k) {
        if (k == 0) {
            Arrays.fill(code, 0);
            return code;
        }
        int sum = 0;
        int n = code.length;
        int[] res = new int[n];
        if (k > 0) {
            for (int i = 0; i < n + k; ++i) {
                sum += code[i % n];
                if (i >= k) {
                    sum -= code[i - k];
                    res[i - k] = sum;
                }
            }
        } else {
            k = -k;
            for (int i = 0; i < n + k; ++i) {
                if (i >= k) {
                    res[i % n] = sum;
                    sum -= code[i - k];
                }
                sum += code[i % n];
            }
        }
        return res;
    }
}
