class Solution {
    fun decrypt(A: IntArray, K: Int): IntArray {
        var N = A.size
        if (K == 0)
            return IntArray(N) { 0 }
        if (K < 0)
            return decrypt(A.reversed().toIntArray(), -K).reversed().toIntArray()
