            return ans;
        }
        else if(k>0){
            for(int i=0;i<code.length;i++){
                ans[i]=sumg(code,i,k);
            }
            return ans;
        }
        else{
            for(int i=0;i<code.length;i++){
                ans[i]=suml(code,i,k*(-1));
            }
            return ans;
        }
    }
}
