class Solution {
public:
    vector<int> decrypt(vector<int>& code, int k) {
        int sz = code.size();
        vector<int> ans(sz, 0);
        if(k == 0) return ans;
        int start = k > 0 ? 1 : sz+k, end = k > 0 ? k : sz-1;
        for(int i=0; i<sz; i++){
            if(i == 0){
                for(int j=start; j<=end; j++)
                    ans[i] += code[j%sz];
            }
            else if(k > 0) ans[i] = ans[i-1] - code[i] + code[(i+k)%sz];
            else ans[i] = ans[i-1] - code[(i-1+sz+k)%sz] + code[i-1];
        }
        return ans;
    }
};
