class Solution {
public:
    vector<int> decrypt(vector<int>& code, int k) {
        int L = code.size(), tmp, j;
        vector<int> decode(L);

        if(k == 0)
            return decode;
        else if(k > 0)
        {
            for(int i = 0; i < L; i ++)
            {
                j = i+1;
                tmp = k;
                while(tmp > 0)
                {
                    if(j == L)
                        j = 0;
                    decode[i] += code[j++];
                    tmp--;
                }
            }
        }
        else
        {
            k *= -1;
            for(int i = 0; i < L; i++)
            {
                j = i - 1;
                tmp = k;
                while(tmp > 0)
                {
                    if(j < 0)
                        j = L - 1;
                    decode[i] += code[j--];
                    tmp--;
                }
            }
        }
        return decode;
    }
};
