object Solution {
    def decrypt(code: Array[Int], k: Int): Array[Int] = {
      val n = code.length
      val res = Array.ofDim[Int](n)
      if (k == 0) res
      else if (k > 0) {
        for (i <- 0 until n) {
          var sum = 0
          for (j <- 1 to k) sum += code((i + j) % n)
