func decrypt(code []int, k int) []int {
    var flag bool

    var result []int

    if k < 0{
        for i := 0; i < len(code) / 2; i ++{
            code[i], code[len(code) - 1 - i] = code[len(code) - 1 - i], code[i]
        }
        flag = true
        k *= -1
    }

    for i := 0; i < len(code); i++{
        var num int
        for j := 0; j < k; j++{
            var val int
            val = i + j + 1
            if val > len(code) - 1{
                val = val - len(code)
            }
            num += code[val]
        }
        result = append(result, num)
    }

    if flag == true{
        for i := 0; i < len(result) / 2; i ++{
            result[i], result[len(result) - 1 - i] = result[len(result) - 1 - i], result[i]
        }
    }
    return result
}
