        for(int i=0;i<n;i++)
        {
            //it is always valid to append b at the 
            if (s[i]=='b') dp[i+1]=dp[i],count_b++;
            else
            {
                //two cases arise
                //case1: we remove a
                //case2: keep a, then count_b number of characters will need to be removed
                dp[i+1]=min(dp[i]+1,count_b);
            }
        }
        return dp[n];

